"use client";

import Link from "next/link";
import {
  Check,
  Copy,
  ExternalLink,
  LogOut,
  MessageCircle,
  Settings2,
  UserRound,
} from "lucide-react";

import { Button } from "@/components/ui/button";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { cn } from "@/registry/default/lib/utils";
import { useCopy } from "@/registry/default/hooks/use-copy";
import { useGradientColors } from "@/registry/default/hooks/use-gradient-colors";

const shortenAddress = (address: string = ""): string =>
  address.length > 9
    ? `${address.slice(0, 6)}...${address.slice(-4)}`
    : address;

const WalletAddress03 = () => {
  const { copied, copy } = useCopy();

  const address = "0x1F6Ac473bfE26A84AcF4e255156B7816245b17aa";
  const shortenedAddress = shortenAddress(address);

  const gradientColors = useGradientColors(address);

  const renderGradientAvatar = () => (
    <div
      className="rounded-full"
      style={{
        width: "1.25rem",
        height: "1.25rem",
        boxShadow: "inset 0 0 0 1px rgba(0, 0, 0, 0.1)",
        backgroundColor: gradientColors[0],
        backgroundImage: `
        radial-gradient(at 66% 77%, ${gradientColors[1]} 0px, transparent 50%),
        radial-gradient(at 29% 97%, ${gradientColors[2]} 0px, transparent 50%),
        radial-gradient(at 99% 86%, ${gradientColors[3]} 0px, transparent 50%),
        radial-gradient(at 29% 88%, ${gradientColors[4]} 0px, transparent 50%)
      `,
      }}
    />
  );

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="ghost" size="icon">
          {renderGradientAvatar()}
        </Button>
      </DropdownMenuTrigger>
      <DropdownMenuContent side="bottom" align="end">
        <DropdownMenuLabel className="flex items-center gap-4 py-3">
          <div className="flex items-center gap-2">
            {renderGradientAvatar()}

            <p className="flex items-center text-sm">{shortenedAddress}</p>
          </div>

          <div className="flex items-center gap-2">
            <Button
              variant="ghost"
              size="icon"
              className="p-0 size-5 hover:bg-transparent text-muted-foreground"
              onClick={() => copy(address)}
            >
              <div
                className={cn(
                  "transition-all",
                  copied ? "scale-100 opacity-100" : "scale-0 opacity-0"
                )}
              >
                <Check color="#10B981" />
              </div>
              <div
                className={cn(
                  "absolute transition-all",
                  copied ? "scale-0 opacity-0" : "scale-100 opacity-100"
                )}
              >
                <Copy />
              </div>
            </Button>

            <Button
              variant="link"
              size="icon"
              className="p-0 size-5 hover:bg-transparent text-muted-foreground hover:text-foreground"
              asChild
            >
              <Link
                href={`https://etherscan.io/address/${address}`}
                target="_blank"
                rel="noreferrer"
              >
                <ExternalLink />
              </Link>
            </Button>
          </div>
        </DropdownMenuLabel>

        <DropdownMenuSeparator />
        <DropdownMenuItem className="cursor-pointer">
          <UserRound />
          <span>Accounts</span>
        </DropdownMenuItem>
        <DropdownMenuItem className="cursor-pointer">
          <Settings2 />
          <span>Settings</span>
        </DropdownMenuItem>
        <DropdownMenuItem className="cursor-pointer">
          <MessageCircle />
          <span>Community</span>
        </DropdownMenuItem>
        <DropdownMenuSeparator />
        <DropdownMenuItem className="cursor-pointer">
          <LogOut />
          <span>Disconnect</span>
        </DropdownMenuItem>
      </DropdownMenuContent>
    </DropdownMenu>
  );
};

export default WalletAddress03;

"use client";

import { useState } from "react";

type UseCopyReturn = {
  copied: boolean;
  copy: (text: string) => Promise<boolean>;
};

export function useCopy(duration = 1500): UseCopyReturn {
  const [copied, setCopied] = useState<boolean>(false);

  const copy = async (text: string) => {
    try {
      await navigator.clipboard.writeText(text);
      setCopied(true);
      setTimeout(() => setCopied(false), duration);
      return true;
    } catch (err) {
      console.error("Failed to copy text: ", err);
      return false;
    }
  };

  return {
    copied,
    copy,
  };
}

import { useMemo } from "react";

/**
 * Custom hook to generate an array of gradient colors based on the given address.
 * Each color is derived by hashing segments of the address string.
 *
 * @param {string} address - The input string used to generate the colors.
 * @returns {string[]} An array of RGB color strings.
 */
export function useGradientColors(address: string): string[] {
  if (!address) {
    throw new Error("Address is required to generate colors.");
  }

  // Memoize the gradient colors for performance optimization
  return useMemo(() => {
    // Extract up to 5 seed strings from the address
    const seedArr = address.match(/.{1,7}/g)?.slice(0, 5);

    if (!seedArr || seedArr.length < 5) {
      throw new Error(
        "Address must be long enough to generate sufficient gradient colors."
      );
    }

    // Generate an array of colors based on the seed array
    return seedArr.map((seed) => {
      let hash = 0;

      // Create a hash from the seed string
      for (let i = 0; i < seed.length; i++) {
        hash = seed.charCodeAt(i) + ((hash << 5) - hash);
        hash &= hash; // Convert to a 32-bit integer
      }

      // Generate RGB values from the hash
      const rgb = Array.from({ length: 3 }, (_, i) => (hash >> (i * 8)) & 255);
      return `rgb(${rgb[0]}, ${rgb[1]}, ${rgb[2]})`;
    });
  }, [address]); // Dependencies array ensures the colors are recalculated only when the address changes
}
